// src/components/ServiceBooking.jsx

import React, { useState, useEffect } from 'react';
import './ServiceBooking.css';
import axios from 'axios';

const ServiceBooking = ({ selectedService = '' }) => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    service_type: selectedService,
    preferred_date: '',
    message: '',
  });

  // Sync prop updates if service type changes (e.g. after tile click)
  useEffect(() => {
    setFormData(prev => ({
      ...prev,
      service_type: selectedService,
    }));
  }, [selectedService]);

  const [submitted, setSubmitted] = useState(false);
  const [ticketId, setTicketId] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setTicketId('');

    const token = localStorage.getItem('access_token');

    if (!token) {
      setError('⚠️ You must be logged in to book a service.');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:8000/api/bookings/',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      setSubmitted(true);
      setTicketId(response.data.ticket_id);
      console.log('✅ Booking submitted successfully:', response.data);
    } catch (error) {
      console.error('❌ Booking submission failed:', error);
      setError('Something went wrong. Try again.');
    }
  };

  return (
    <div className="book-service-form">
      <h2>Book a Smart Home Service</h2>
      {submitted ? (
        <div className="success-message">
          <p>✅ Thank you! Your service ticket has been created.</p>
          <p><strong>Ticket ID:</strong> {ticketId}</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input name="name" placeholder="Name" onChange={handleChange} value={formData.name} required />
          <input name="phone" placeholder="Phone" onChange={handleChange} value={formData.phone} required />

          {/* ✅ Dropdown for Service Type */}
          <select name="service_type" onChange={handleChange} value={formData.service_type} required>
            <option value="">Select Service Type</option>
            <option value="Basic Package">Basic Package</option>
            <option value="Plus Package">Plus Package</option>
            <option value="Full Automation">Full Automation</option>
          </select>

          <input type="date" name="preferred_date" onChange={handleChange} value={formData.preferred_date} required />
          <textarea name="message" placeholder="Additional Message (Optional)" onChange={handleChange} value={formData.message} />
          <button type="submit">Submit</button>
          {error && <p className="error-message" style={{ color: '#F44336' }}>{error}</p>}
        </form>
      )}
    </div>
  );
};

export default ServiceBooking;
