// src/components/ServiceBooking.jsx

import React, { useState } from 'react';
import './ServiceBooking.css';
import axios from 'axios';
import { useAuth } from '../Context/AuthContext'; // Import useAuth hook

const ServiceBooking = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    service_type: '',
    preferred_date: '',
    message: '',
  });

  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');

  // --- NEW: Get the getAccessToken function from AuthContext ---
  const { getAccessToken, isAuthenticated } = useAuth(); // Also get isAuthenticated for convenience

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // --- NEW: Use getAccessToken from context instead of directly from localStorage ---
    const accessToken = getAccessToken();

    if (!accessToken) {
      setError('You must be logged in to book a service.');
      console.error('No access token found. User is not logged in.');
      // Optional: If you use react-router-dom, you might redirect to login here:
      // navigate('/login');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:8000/api/bookings', // Ensure this URL matches your NestJS booking endpoint
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${accessToken}`
          }
        }
      );

      setSubmitted(true);
      console.log('Booking submitted successfully:', response.data);

    } catch (error) {
      console.error('Booking submission failed:', error);
      if (error.response) {
        if (error.response.status === 401 || error.response.status === 403) {
          setError('Authentication failed. Please log in again. (Your session might have expired)');
          // Consider using logout() from AuthContext here to clear expired token
          // logout();
        } else if (error.response.data && error.response.data.message) {
          if (Array.isArray(error.response.data.message)) {
              setError(`Submission failed: ${error.response.data.message.join(', ')}`);
          } else {
              setError(`Submission failed: ${error.response.data.message}`);
          }
        } else {
          setError('Booking submission failed. Please try again.');
        }
      } else if (error.request) {
        setError('No response from server. Please check your internet connection and backend server status.');
      } else {
        setError('An unexpected error occurred. Please try again.');
      }
    }
  };

  return (
    <div className="book-service-form">
      <h2>Book a Smart Home Service</h2>
      {/* Optional: Conditionally render form based on authentication status */}
      {!isAuthenticated && <p className="error-message">Please log in to book a service.</p>}

      {submitted ? (
        <p className="success-message">Thank you! We will contact you shortly.</p>
      ) : (
        // Only render form if user is authenticated (optional, but good UX)
        isAuthenticated ? (
          <form onSubmit={handleSubmit}>
            <input name="name" placeholder="Name" onChange={handleChange} required />
            <input name="phone" placeholder="Phone" onChange={handleChange} required />
            <input name="service_type" placeholder="Type of Service" onChange={handleChange} required />
            <input name="preferred_date" type="date" onChange={handleChange} required />
            <textarea name="message" placeholder="Additional Message (Optional)" onChange={handleChange} />
            <button type="submit">Submit</button>
            {error && <p className="error-message" style={{ color: '#F44336', marginTop: '15px' }}>{error}</p>}
          </form>
        ) : (
          // Display a message or redirect if not authenticated
          <p>Please <Link to="/login">login</Link> to book a service.</p>
        )
      )}
    </div>
  );
};

export default ServiceBooking;
