// src/components/ServiceBooking.jsx

import React, { useState } from 'react';
import './ServiceBooking.css';
import axios from 'axios';

const ServiceBooking = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    service_type: '',
    preferred_date: '',
    message: '',
  });

  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(''); // Added state for handling submission errors

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors

    // --- NEW: Get Access Token from localStorage ---
    const accessToken = localStorage.getItem('accessToken');

    if (!accessToken) {
      setError('You must be logged in to book a service.');
      console.error('No access token found. User is not logged in.');
      return; // Stop the submission if no token
    }

    try {
      // --- NEW: Include Authorization header with the Access Token ---
      const response = await axios.post(
        'http://localhost:8000/api/bookings/', // Use the new backend endpoint
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${accessToken}` // Attach the JWT token
          }
        }
      );

      setSubmitted(true);
      console.log('Booking submitted successfully:', response.data);

    } catch (error) {
      console.error('Booking submission failed:', error);
      // More detailed error handling for the user
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        if (error.response.status === 401) {
          setError('You are not authorized. Please log in again.');
        } else if (error.response.data && typeof error.response.data === 'object') {
          // If the server sends back specific validation errors
          const errorMessages = Object.values(error.response.data).flat().join(' ');
          setError(`Submission failed: ${errorMessages}`);
        } else {
          setError('Booking submission failed. Please try again.');
        }
      } else if (error.request) {
        // The request was made but no response was received
        setError('No response from server. Please check your internet connection.');
      } else {
        // Something else happened in setting up the request that triggered an Error
        setError('An unexpected error occurred. Please try again.');
      }
    }
  };

  return (
    <div className="book-service-form">
      <h2>Book a Smart Home Service</h2>
      {submitted ? (
        <p className="success-message">Thank you! We will contact you shortly.</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <input name="name" placeholder="Name" onChange={handleChange} required />
          <input name="phone" placeholder="Phone" onChange={handleChange} required />
          <input name="service_type" placeholder="Type of Service" onChange={handleChange} required />
          <input name="preferred_date" type="date" onChange={handleChange} required />
          <textarea name="message" placeholder="Additional Message (Optional)" onChange={handleChange} />
          <button type="submit">Submit</button>
          {error && <p className="error-message" style={{ color: '#F44336', marginTop: '15px' }}>{error}</p>} {/* Display errors */}
        </form>
      )}
    </div>
  );
};

export default ServiceBooking;
