import React, { useEffect, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();
  const [username, setUsername] = useState(null);

  // Run this effect whenever location changes (includes page refresh or route change)
  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      try {
        const { name } = JSON.parse(userData);
        setUsername(name);
      } catch {
        localStorage.removeItem('user');
        setUsername(null);
      }
    } else {
      setUsername(null);
    }
  }, [location]);  // <-- added location as dependency

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    setUsername(null);
    navigate('/login');
  };

  return (
    <div className="navbar">
      <Link to="/" className="navbar-logo">NeuraNest</Link>
      <div className="nav-links">
        <Link to="/" className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}>Home</Link>
        <Link to="/about" className={`nav-link ${location.pathname === '/about' ? 'active' : ''}`}>About</Link>
        <Link to="/services" className={`nav-link ${location.pathname === '/services' ? 'active' : ''}`}>Services</Link>
        <Link to="/contact" className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}>Contact</Link>

        {username ? (
          <div className="user-info">
            <span className="nav-link user-name">Welcome, {username}</span>
            <button onClick={handleLogout} className="nav-link logout-button">Logout</button>
          </div>
        ) : (
          <Link to="/login" className={`nav-link ${location.pathname === '/login' ? 'active' : ''}`}>Login</Link>
        )}
      </div>
    </div>
  );
}

export default Navbar;
