// frontend/src/components/Navbar.js
import React, { useEffect, useState } from 'react'; // Keep useState for local state if needed, but remove for username
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';
import { useAuth } from '../context/AuthContext'; // Import the useAuth hook

function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();
  // REMOVED: const [username, setUsername] = useState(null);
  // Get user and logout function from AuthContext
  const { user, logout } = useAuth(); 

  // REMOVED: useEffect that reads from localStorage
  // useEffect(() => {
  //   const userData = localStorage.getItem('user');
  //   if (userData) {
  //     try {
  //       const { name } = JSON.parse(userData);
  //       setUsername(name);
  //     } catch {
  //       localStorage.removeItem('user');
  //       setUsername(null);
  //     }
  //   } else {
  //     setUsername(null);
  //   }
  // }, [location]);

  const handleLogout = () => {
    // REMOVED: localStorage.removeItem calls
    // localStorage.removeItem('user');
    // localStorage.removeItem('accessToken');
    // localStorage.removeItem('refreshToken');
    // REMOVED: setUsername(null);
    logout(); // Call the logout function from AuthContext to clear in-memory state
    navigate('/login'); // Redirect to the login page after logging out
  };

  return (
    <div className="navbar">
      <Link to="/" className="navbar-logo">NeuraNest</Link>
      <div className="nav-links">
        <Link to="/" className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}>Home</Link>
        <Link to="/about" className={`nav-link ${location.pathname === '/about' ? 'active' : ''}`}>About</Link>
        <Link to="/services" className={`nav-link ${location.pathname === '/services' ? 'active' : ''}`}>Services</Link>
        <Link to="/contact" className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}>Contact</Link>

        {/* Conditional rendering based on 'user' from AuthContext */}
        {user ? (
          <div className="user-info">
            {/* Display user name from context, fallback to email or 'User' */}
            <span className="nav-link user-name">Welcome, {user.name || user.email || 'User'}</span>
            <button onClick={handleLogout} className="nav-link logout-button">Logout</button>
          </div>
        ) : (
          <Link to="/login" className={`nav-link ${location.pathname === '/login' ? 'active' : ''}`}>Login</Link>
        )}
      </div>
    </div>
  );
}

export default Navbar;
