// frontend/src/Context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios'; // Import axios for interceptors

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [authToken, setAuthToken] = useState(() => {
    try {
      // *** CHANGE TO localStorage ***
      const storedToken = localStorage.getItem('authToken');
      return storedToken ? JSON.parse(storedToken) : null;
    } catch (error) {
      console.error("Failed to parse authToken from localStorage:", error);
      return null;
    }
  });

  const [user, setUser] = useState(() => {
    try {
      // *** CHANGE TO localStorage ***
      const storedUser = localStorage.getItem('user');
      return storedUser ? JSON.parse(storedUser) : null;
    } catch (error) {
      console.error("Failed to parse user from localStorage:", error);
      return null;
    }
  });

  const [loading, setLoading] = useState(false);

  const login = (tokenData, userData) => {
    setAuthToken(tokenData);
    setUser(userData);
    // *** CHANGE TO localStorage ***
    localStorage.setItem('authToken', JSON.stringify(tokenData));
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const logout = () => {
    setAuthToken(null);
    setUser(null);
    // *** CHANGE TO localStorage ***
    localStorage.removeItem('authToken');
    localStorage.removeItem('user');
  };

  useEffect(() => {
    const requestInterceptor = axios.interceptors.request.use(
      config => {
        if (authToken && authToken.access) {
          config.headers.Authorization = `Bearer ${authToken.access}`;
        }
        return config;
      },
      error => Promise.reject(error)
    );

    return () => {
      axios.interceptors.request.eject(requestInterceptor);
    };
  }, [authToken]);

  return (
    <AuthContext.Provider value={{ user, authToken, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};
