// frontend/src/Pages/LogIn.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './LogIn.css';
import { useAuth } from '../Context/AuthContext'; // Note the capital 'C' in Context

const LogIn = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth(); // Get the login function from AuthContext

  // Helper: fetch username by email (Keep this if your backend needs username for login)
  const fetchUsernameByEmail = async (email) => {
    try {
      const response = await fetch(`http://127.0.0.1:8000/api/get-username/?email=${encodeURIComponent(email)}`);
      if (!response.ok) {
        // If the email is not found, throw an error to be caught by the outer try-catch
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Email not found');
      }
      const data = await response.json();
      return data.username;
    } catch (err) {
      // Re-throw or handle the error appropriately
      throw new Error(err.message || 'Failed to fetch username');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Step 1: Get username from backend (if required by your backend's login endpoint)
      const username = await fetchUsernameByEmail(email);

      // Step 2: Perform the actual login request
      const response = await fetch('http://127.0.0.1:8000/api/login/', { // Adjust to your login endpoint
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }), // Use username obtained above
      });

      const data = await response.json();

      if (response.ok) {
        // --- IMPORTANT CHANGE HERE ---
        // REMOVE all localStorage.setItem calls
        // Instead, call the 'login' function from AuthContext to set in-memory state
        login(
          { access: data.access, refresh: data.refresh }, // Pass token data
          { email, name: username }                       // Pass user data (email & username)
        );

        console.log('✅ Login successful!');
        navigate('/'); // Redirect to homepage
      } else {
        // Backend returned an error (e.g., 400 Bad Request, 401 Unauthorized)
        setError(data.detail || 'Invalid credentials');
      }
    } catch (err) {
      // Catch any network errors or errors thrown by fetchUsernameByEmail
      setError(err.message || 'Something went wrong');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2 className="login-title">Welcome Back</h2>
        <p className="login-subtitle">Please enter your credentials to log in</p>

        <form onSubmit={handleLogin} className="login-form">
          <div className="form-group">
            <label htmlFor="email" className="form-label">Email Address</label>
            <input
              type="email"
              id="email"
              className="form-input"
              placeholder="example@domain.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              id="password"
              className="form-input"
              placeholder="••••••••"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          {error && <p className="error-message">{error}</p>}

          <button type="submit" className="login-button">Login</button>
        </form>

        <div className="login-footer">
          <p>Don’t have an account? <Link to="/signup" className="signup-link">Sign Up</Link></p>
        </div>
      </div>
    </div>
  );
};

export default LogIn;
