// frontend/src/Context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios'; // Import axios for interceptors

// Create the AuthContext
const AuthContext = createContext(null);

// AuthProvider component to wrap your application
export const AuthProvider = ({ children }) => {
  // State to hold the authentication token and user info (in memory only)
  const [authToken, setAuthToken] = useState(null);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false); // Can be used for async operations if needed

  // --- Login Function ---
  // This function will be called by your LogIn.js component upon successful login.
  // It updates the in-memory state.
  const login = (tokenData, userData) => {
    setAuthToken(tokenData); // Store token data (e.g., { access: '...', refresh: '...' })
    setUser(userData);      // Store user data (e.g., { email: '...', name: '...' })
    // IMPORTANT: NO localStorage.setItem CALLS HERE for non-persistence
  };

  // --- Logout Function ---
  // This function will be called by your Navbar or any logout button.
  // It clears the in-memory authentication state.
  const logout = () => {
    setAuthToken(null);
    setUser(null);
    // IMPORTANT: NO localStorage.removeItem CALLS HERE
  };

  // --- Axios Interceptor for automatically attaching token to requests ---
  // This useEffect hook configures Axios to include the access token
  // in the Authorization header for all outgoing requests, but only if a token exists in memory.
  useEffect(() => {
    const requestInterceptor = axios.interceptors.request.use(
      config => {
        if (authToken && authToken.access) {
          config.headers.Authorization = `Bearer ${authToken.access}`;
        }
        return config; // Return the modified config
      },
      error => Promise.reject(error) // Handle any request errors
    );

    // Cleanup function: This runs when the component unmounts or when authToken changes.
    // It ejects the interceptor to prevent memory leaks or stale interceptors.
    return () => {
      axios.interceptors.request.eject(requestInterceptor);
    };
  }, [authToken]); // Dependency array: re-run this effect if authToken changes

  // Provide the authentication state and functions to children components
  return (
    <AuthContext.Provider value={{ user, authToken, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to easily consume the AuthContext in any functional component
export const useAuth = () => {
  return useContext(AuthContext);
};
