// frontend/src/components/ServiceHistory.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../Context/AuthContext';

const ServiceHistory = () => {
  const { authToken } = useAuth();
  const [history, setHistory] = useState([]);
  const [error, setError] = useState('');

  const fetchHistory = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/service-history/');
      setHistory(response.data);
    } catch (err) {
      console.error('Error fetching service history:', err);
      setError('⚠️ You must be logged in to view your service history.');
    }
  };

  useEffect(() => {
    if (authToken?.access) {
      fetchHistory();
    } else {
      setError('⚠️ You must be logged in to view your service history.');
    }
  }, [authToken]);

  return (
    <div className="service-history">
      <h2>Your Service History</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {history.length === 0 && !error ? (
        <p>No service records found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Service Type</th>
              <th>Preferred Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {history.map((booking) => (
              <tr key={booking.id}>
                <td>{booking.ticket_id}</td>
                <td>{booking.service_type}</td>
                <td>{booking.preferred_date}</td>
                <td>{booking.status || 'Active'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ServiceHistory;
