// src/components/Login.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Assuming you use react-router-dom for navigation
import './Login.css'; // Assuming you have some basic styling

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Hook for navigation

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Adjust this URL to your NestJS login endpoint
      const response = await axios.post('http://localhost:8000/api/auth/login', {
        email,
        password,
      });

      const { accessToken } = response.data; // NestJS typically returns { accessToken: 'your_jwt_token' }

      if (accessToken) {
        localStorage.setItem('accessToken', accessToken); // Store the token
        console.log('Login successful! Token stored.');
        // Redirect to a dashboard or the service booking page
        navigate('/book-service');
      } else {
        setError('Login successful, but no token received.');
      }

    } catch (error) {
      console.error('Login failed:', error.response?.data || error.message);
      if (error.response && error.response.data && error.response.data.message) {
        setError(error.response.data.message);
      } else {
        setError('Login failed. Please check your credentials.');
      }
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        {error && <p className="error-message" style={{ color: '#F44336', marginTop: '15px' }}>{error}</p>}
      </form>
    </div>
  );
};

export default Login;
