// src/components/ServiceBooking.jsx

import React, { useState } from 'react';
import './ServiceBooking.css';
import axios from 'axios';

const ServiceBooking = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    service_type: '',
    preferred_date: '',
    message: '',
  });

  const [submitted, setSubmitted] = useState(false);
  const [ticketId, setTicketId] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setTicketId('');

    try {
      const response = await axios.post(
        'http://localhost:8000/api/bookings/',
        formData
      );

      setSubmitted(true);
      setTicketId(response.data.ticket_id); // <-- Get ticket ID from response
      console.log('Booking submitted successfully:', response.data);

    } catch (error) {
      console.error('Booking submission failed:', error);
      if (error.response) {
        if (error.response.status === 401 || error.response.status === 403) {
          setError('Authentication failed. Please log in again.');
        } else if (error.response.data && error.response.data.message) {
          const message = error.response.data.message;
          setError(`Submission failed: ${Array.isArray(message) ? message.join(', ') : message}`);
        } else {
          setError('Booking submission failed. Please try again.');
        }
      } else if (error.request) {
        setError('No response from server. Please check your internet connection.');
      } else {
        setError('An unexpected error occurred. Please try again.');
      }
    }
  };

  return (
    <div className="book-service-form">
      <h2>Book a Smart Home Service</h2>
      {submitted ? (
        <div className="success-message">
          <p>Thank you! Your service ticket has been created.</p>
          <p><strong>Ticket ID:</strong> {ticketId}</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            name="name"
            placeholder="Name"
            onChange={handleChange}
            value={formData.name}
            required
          />
          <input
            name="phone"
            placeholder="Phone"
            onChange={handleChange}
            value={formData.phone}
            required
          />
          <input
            name="service_type"
            placeholder="Type of Service"
            onChange={handleChange}
            value={formData.service_type}
            required
          />
          <input
            name="preferred_date"
            type="date"
            onChange={handleChange}
            value={formData.preferred_date}
            required
          />
          <textarea
            name="message"
            placeholder="Additional Message (Optional)"
            onChange={handleChange}
            value={formData.message}
          />
          <button type="submit">Submit</button>
          {error && <p className="error-message" style={{ color: '#F44336', marginTop: '15px' }}>{error}</p>}
        </form>
      )}
    </div>
  );
};

export default ServiceBooking;
